{% set name_len = company_name|length %}

{% if name_len > 40 %}
    {# truncate to 35 chars and append "..." #}
    {% set company_name_final = company_name[:35] ~ "..." %}
    {% set company_font_height = 42 %}
    {% set company_font_width  = 32 %}

{% elif name_len > 32 %}
    {# shrink to 50% #}
    {% set company_name_final = company_name %}
    {% set company_font_height = 42 %}
    {% set company_font_width  = 32 %}

{% elif name_len > 26 %}
    {# shrink to 75% #}
    {% set company_name_final = company_name %}
    {% set company_font_height = 64 %}
    {% set company_font_width  = 50 %}

{% else %}
    {# original size #}
    {% set company_name_final = company_name %}
    {% set company_font_height = 85 %}
    {% set company_font_width  = 65 %}
{% endif %}




{# ---------------------------- #}
{#   ITEM NAME FONT & SPLIT     #}
{# ---------------------------- #}

{% set name = item_name %}
{% set name_len = name|length %}

{% if name_len > 30 %}
    {# shrink font to 50% #}
    {% set item_font_height = 45 %}
    {% set item_font_width  = 30 %}
{% else %}
    {# normal font #}
    {% set item_font_height = 60 %}
    {% set item_font_width  = 40 %}
{% endif %}

{# ---------- SPLIT LOGIC ---------- #}
{% if name_len > 18 %}
    {% set cutoff = 18 %}
    {% set part1 = name[:cutoff] %}

    {# find last space before cutoff #}
    {% set last_space = part1.rfind(" ") %}

    {% if last_space != -1 %}
        {# split at space #}
        {% set line1 = name[:last_space] %}
        {% set line2 = name[last_space+1:] %}
    {% else %}
        {# force split at 15 chars #}
        {% set line1 = name[:cutoff] %}
        {% set line2 = name[cutoff:] %}
    {% endif %}
{% else %}
    {% set line1 = name %}
    {% set line2 = "" %}
{% endif %}




